import React, { useState, useEffect } from 'react';
import { BrainCircuit, Loader, Wand2, X, Sun, Moon } from 'lucide-react';
import ThemeToggle from './components/ThemeToggle';
import FormInput from './components/FormInput';
import FormSelect from './components/FormSelect';
import MultiSelect from './components/MultiSelect';

function App() {
  // Example state for demonstration
  const [theme, setTheme] = useState('light');
  const [inputValue, setInputValue] = useState('');
  const [selectValue, setSelectValue] = useState('Beginner');
  const [multiSelectValues, setMultiSelectValues] = useState(['HTML', 'CSS']);

  const selectOptions = ['Beginner', 'Intermediate', 'Advanced'];
  const multiSelectOptions = ['HTML', 'CSS', 'JavaScript', 'React'];

  return (
    <div className={theme === 'dark' ? 'dark bg-gray-900 text-white min-h-screen' : 'bg-gray-50 text-gray-900 min-h-screen'}>
      <div className="container mx-auto p-4">
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold flex items-center gap-2">
            <BrainCircuit size={32} /> Portfolio Project Generator
          </h1>
          <ThemeToggle theme={theme} toggleTheme={() => setTheme(theme === 'light' ? 'dark' : 'light')} />
        </header>
        <main className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <section className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow border">
            <h2 className="text-xl font-semibold mb-4">Demo Form</h2>
            <form className="space-y-4">
              <FormInput label="Project Name" placeholder="Enter project name" value={inputValue} onChange={setInputValue} />
              <FormSelect label="Experience Level" value={selectValue} onChange={setSelectValue} options={selectOptions} />
              <MultiSelect label="Skills" options={multiSelectOptions} selectedOptions={multiSelectValues} onChange={setMultiSelectValues} />
            </form>
          </section>
          <section className="bg-white dark:bg-gray-800 p-6 rounded-xl shadow border flex flex-col items-center justify-center">
            <h2 className="text-xl font-semibold mb-4">Preview</h2>
            <div className="mb-2">Theme: {theme === 'dark' ? <Moon size={20} /> : <Sun size={20} />}</div>
            <div className="mb-2">Project Name: <strong>{inputValue}</strong></div>
            <div className="mb-2">Experience Level: <strong>{selectValue}</strong></div>
            <div className="mb-2">Skills: {multiSelectValues.map(skill => (
              <span key={skill} className="inline-block bg-indigo-100 dark:bg-indigo-900/50 text-indigo-800 dark:text-indigo-300 px-2 py-1 rounded-full mr-2 text-sm">{skill}</span>
            ))}</div>
          </section>
        </main>
      </div>
    </div>
  );
}

export default App;
